import { useState } from "react";
import FilmDetail from "./FilmDetail";
import './FilmLibrary.css';
import FilmRow from "./FilmRow";
import TMDB from "./TMDB";

function FilmLibrary() {

  const [selectedFilm, setSelectedFilm] = useState(TMDB.films[0])
  const [favFilm, setFavFilm] =  useState([])

  console.log(favFilm)
  return (
    <div className="FilmLibrary">
      <div className="film-list">
        <h1 className="section-title">FILMS</h1>
        <div className="film-list-filters">
          <button className="film-list-filter is-active">
            ALL
            <span className="section-count">{TMDB.films.length}</span>
          </button>
          <button className="film-list-filter">
            FAVES
            <span className="section-count">{favFilm.length}</span>
          </button>
        </div>
        {TMDB.films.map((film, index) =>
          <FilmRow key={index}
              id={film.id}
              imgURL={film.poster_path}
              title={film.title}
              year={new Date(film.release_date).getFullYear()}
              handleSelectFilm={()=>setSelectedFilm(TMDB.films[index])}
              handleSelectFavFilm={()=>setFavFilm(favFilm=>[...favFilm,film.id])}
          />        
        )}

      </div>

      <div className="film-details">
        <h1 className="section-title">{selectedFilm.title}</h1>
        <FilmDetail
            title={selectedFilm.title}
            overview={selectedFilm.overview}
            poster_path={selectedFilm.poster_path}
            backdrop_path={selectedFilm.backdrop_path}
        />
      </div>
    </div>
  );
}

export default FilmLibrary
